            let $childComponent = childrenWithIDs.eq(i);
            id = $childComponent.attr('id');
            if (id) {
                const newID = $.fn.uid();
                $childComponent.attr('id', newID);
                $childComponent.attr('data-id', newID);

                idTranslationTable[id] = newID;
            }
